cap = cv2.VideoCapture(video_paths[3])

while cap.isOpened():
    ret, img = cap.read()
    img = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)
    #cv2.equalizeHist(img_hist, img_hist)
    img_masked = opening_masking(img)
